binding.script:
if (getprop("/controls/lighting/beacon") and getprop("/systems/electrical/amps")>0)
setprop("/systems/electrical/amps", getprop("/systems/electrical/amps")+4);
if (!getprop("/controls/lighting/beacon") and getprop("/systems/electrical/amps")>0)
setprop("/systems/electrical/amps", getprop("/systems/electrical/amps")-4);
controls.click(2);

if (getprop("/controls/lighting/nav-lights") and getprop("/systems/electrical/amps")>0)
setprop("/systems/electrical/amps", getprop("/systems/electrical/amps")+5);
if (!getprop("/controls/lighting/nav-lights") and getprop("/systems/electrical/amps")>0)
setprop("/systems/electrical/amps", getprop("/systems/electrical/amps")-5);
controls.click(2);

if (getprop("/controls/lighting/logo-lights") and getprop("/systems/electrical/amps")>0)
setprop("/systems/electrical/amps", getprop("/systems/electrical/amps")+4);
if (!getprop("/controls/lighting/logo-lights") and getprop("/systems/electrical/amps")>0)
setprop("/systems/electrical/amps", getprop("/systems/electrical/amps")-4);
controls.click(2);

if (getprop("/controls/lighting/wing-lights") and getprop("/systems/electrical/amps")>0)
setprop("/systems/electrical/amps", getprop("/systems/electrical/amps")+4);
if (!getprop("/controls/lighting/wing-lights") and getprop("/systems/electrical/amps")>0)
setprop("/systems/electrical/amps", getprop("/systems/electrical/amps")-4);
controls.click(2);

if (getprop("/controls/lighting/landing-light[0]") and getprop("/systems/electrical/amps")>0)
setprop("/systems/electrical/amps", getprop("/systems/electrical/amps")+7);
if (!getprop("/controls/lighting/landing-light[0]") and getprop("/systems/electrical/amps")>0)
setprop("/systems/electrical/amps", getprop("/systems/electrical/amps")-7);
controls.click(2);

if (getprop("/controls/lighting/landing-light[1]") and getprop("/systems/electrical/amps")>0)
setprop("/systems/electrical/amps", getprop("/systems/electrical/amps")+7);
if (!getprop("/controls/lighting/landing-light[1]") and getprop("/systems/electrical/amps")>0)
setprop("/systems/electrical/amps", getprop("/systems/electrical/amps")-7);
controls.click(2);

if (getprop("/controls/lighting/landing-light[2]") and getprop("/systems/electrical/amps")>0)
setprop("/systems/electrical/amps", getprop("/systems/electrical/amps")+7);
if (!getprop("/controls/lighting/landing-light[2]") and getprop("/systems/electrical/amps")>0)
setprop("/systems/electrical/amps", getprop("/systems/electrical/amps")-7);
controls.click(2);

var dump_fuel = func {
if (getprop("/aaa/jettison")==1){
if (getprop("/consumables/fuel/tank[0]/level-gal_us")>1000)
setprop("/consumables/fuel/tank[0]/level-gal_us", getprop("/consumables/fuel/tank[0]/level-gal_us")-3);
if (getprop("/consumables/fuel/tank[1]/level-gal_us")>1000)
setprop("/consumables/fuel/tank[1]/level-gal_us", getprop("/consumables/fuel/tank[1]/level-gal_us")-3);
if (getprop("/consumables/fuel/tank[2]/level-gal_us")>1000)
setprop("/consumables/fuel/tank[2]/level-gal_us", getprop("/consumables/fuel/tank[2]/level-gal_us")-3);				
settimer(dump_fuel, 0.5);
}
}
if (getprop("/aaa/jettison")==0) setprop("/aaa/jettison", 1);
else if (getprop("/aaa/jettison")==1) setprop("/aaa/jettison", 0);				
dump_fuel();
controls.click(2);

var xfeed_pumping = func {
if (getprop("/consumables/fuel/tank[0]/level-gal_us")>200 or getprop("/consumables/fuel/tank[2]/level-gal_us")>200){
if (getprop("/consumables/fuel/tank[0]/level-gal_us")<getprop("/consumables/fuel/tank[2]/level-gal_us")){
setprop("/consumables/fuel/tank[0]/level-gal_us", getprop("/consumables/fuel/tank[0]/level-gal_us")+2);
setprop("/consumables/fuel/tank[2]/level-gal_us", getprop("/consumables/fuel/tank[2]/level-gal_us")-2);
}
if (getprop("/consumables/fuel/tank[0]/level-gal_us")>getprop("/consumables/fuel/tank[2]/level-gal_us")){
setprop("/consumables/fuel/tank[2]/level-gal_us", getprop("/consumables/fuel/tank[2]/level-gal_us")+2);
setprop("/consumables/fuel/tank[0]/level-gal_us", getprop("/consumables/fuel/tank[0]/level-gal_us")-2);				
}
}
if (getprop("/aaa/xfeed_1")==1 and getprop("/aaa/xfeed_2")==1){
setprop("/aaa/xfeed_progr", 1);
settimer(xfeed_pumping, 0.5);
}
}
if (getprop("/aaa/xfeed_1")==1 and getprop("/aaa/xfeed_2")==1)
xfeed_pumping();
if (getprop("/aaa/xfeed_1")==0 or getprop("/aaa/xfeed_2")==0)
setprop("/aaa/xfeed_progr", 0);
controls.click(2);

var apuconsume = func {
if (getprop("/controls/electric/APU-generator")==1 and getprop("/consumables/fuel/tank[0]/level-gal_us")>1){
setprop("/consumables/fuel/tank[0]/level-gal_us", getprop("/consumables/fuel/tank[0]/level-gal_us")-0.0118333);
settimer(apuconsume, 0.5);
}
}
var aputurnon = func{
if (getprop("/controls/electric/APU-button")==1 and (getprop("/systems/electrical/left-bus")>20 or !getprop("/engines/engine[0]/cutoff") or !getprop("/engines/engine[1]/cutoff"))){
setprop("/controls/electric/APU-generator", 1);
apuconsume();
}
}
if (getprop("/controls/electric/APU-button")==1)
settimer(aputurnon, 40);
controls.click(2);

var apushutdown = func {
setprop("/controls/electric/APU-generator", 0);
}
if (getprop("/controls/electric/APU-button")==-1 and getprop("/controls/electric/APU-generator")==1)
settimer(apushutdown, 21);
controls.click(2);

var deicing = func {
if (getprop("/aaa/windows_ice/left2")>0.05 and getprop("/aaa/deice/di1")==1){
setprop("/aaa/windows_ice/left2", getprop("/aaa/windows_ice/left2")-0.1);
setprop("/aaa/windows_ice/left3", getprop("/aaa/windows_ice/left3")-0.1);
settimer(deicing, 30);
}
}
if (getprop("/aaa/deice/di1")==1)
deicing();
controls.click(2);

var deicing = func {
if (getprop("/aaa/windows_ice/left1")>0.05 and getprop("/aaa/deice/di2")==1){
setprop("/aaa/windows_ice/left1", getprop("/aaa/windows_ice/left1")-0.1);
settimer(deicing, 30);
}
}
if (getprop("/aaa/deice/di2")==1)
deicing();
controls.click(2);

var deicing = func {
if (getprop("/aaa/windows_ice/right1")>0.05 and getprop("/aaa/deice/di3")==1){
setprop("/aaa/windows_ice/right1", getprop("/aaa/windows_ice/right1")-0.1);
settimer(deicing, 30);
}
}
if (getprop("/aaa/deice/di3")==1)
deicing();
controls.click(2);

var deicing = func {
if (getprop("/aaa/windows_ice/right2")>0.05 and getprop("/aaa/deice/di4")==1){
setprop("/aaa/windows_ice/right2", getprop("/aaa/windows_ice/right2")-0.1);
setprop("/aaa/windows_ice/right3", getprop("/aaa/windows_ice/right3")-0.1);
settimer(deicing, 30);
}
}
if (getprop("/aaa/deice/di4")==1)
deicing();
controls.click(2);

if (getprop("/aaa/oh/adiru") and getprop("/systems/electrical/amps")>0)
setprop("/systems/electrical/amps", getprop("/systems/electrical/amps")+14);
if (!getprop("/aaa/oh/adiru") and getprop("/systems/electrical/amps")>0)
setprop("/systems/electrical/amps", getprop("/systems/electrical/amps")-14);
controls.click(2);
